mapper.select:
select 

from user_order
where orderId = #{orderid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=INTEGER}

mapper.delete:
delete from user_order
where orderId = #{orderid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=INTEGER}

mapper.update:
update user_order


    rateFlag = #{rateflag,jdbcType=BIT},


    rate = #{rate,jdbcType=REAL},


where orderId = #{orderid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=INTEGER}

update user_order
set rateFlag = #{rateflag,jdbcType=BIT},
  rate = #{rate,jdbcType=REAL}
where orderId = #{orderid,jdbcType=INTEGER}
  and userId = #{userid,jdbcType=INTEGER}
